var button=document.querySelector(".button-search"),popup=document.querySelector(".search-hotel-modal"),form=popup.querySelector("form"),arrival=popup.querySelector("[name=arrival]"),departure=popup.querySelector("[name=departure]"),adults=popup.querySelector("[name=adults]"),children=popup.querySelector("[name=children]");button.addEventListener("click",function(e){e.preventDefault(),popup.classList.toggle("modal-show"),arrival.focus()}),form.addEventListener("submit",function(e){arrival.value&&departure.value&&adults.value&&children.value||(e.preventDefault(),console.log("Нужно указать даты заеда и выезда, а также колличество взрослых и детей!"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("modal-show")&&(e.preventDefault(),popup.classList.remove("modal-show"))});